import { HttpClient } from '@angular/common/http';
import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { ActivatedRoute, ActivatedRouteSnapshot } from '@angular/router';

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent implements OnInit{

actorDetails:any;
actorId:any;
constructor(private http:HttpClient, private active:ActivatedRoute,
  private cdr:ChangeDetectorRef){}  
  ngOnInit(): void {

    this.active.params.subscribe((id)=>{
      this.actorId=id['id']
    })


    this.http.get<any>(`https://api.themoviedb.org/3/person/${this.actorId}?api_key=c3a444538ac89d0f62cf2c1a5f74f6eb`).subscribe((data)=>{
      this.actorDetails=data
      this.cdr.detectChanges()
      console.log(this.actorDetails)
      console.log(this.actorId)
    })

  }

getGenderLabel(gender:number):string{
  return gender === 1 ? 'Female':gender ===2? 'Male':'Unknown';
}  


}
<div class="container">
  <div class="row">
    <ng-component>
      <div class="col-md-4">
        <img
          [src]="[
            'https://image.tmdb.org/t/p/w500/' + actorDetails.poster_path
          ]"
          alt=""
        />
      </div>

      <div class="col-md-8">
        <div class="actor-name">Name: {{ actorDetails.name }}</div>
        <div class="known-as">
          <span>Also Known As: </span>
          <span *ngFor="let names of actorDetails.also_known_as"
            >{{ names }},</span
          >
        </div>
        <div class="actor-biography">
          Biography:
          <p>{{ actorDetails.biography }}</p>
        </div>
        <div class="actor-birthday">Birth Day: {{ actorDetails.birthday }}</div>
        <div class="birth-place">
          Birth Place: {{ actorDetails.place_of_birth }}
        </div>
        <div class="actor-deathday" *ngIf="actorDetails.deathday">
          Death: {{ actorDetails.deathday }}
        </div>

        <div class="actor-gender">
          Gender: {{ getGenderLabel(actorDetails.gender) }}
        </div>
        <div class="department">
          Profession: {{ actorDetails.known_for_department }}
        </div>
        <div class="popularity">
          Popularity: {{ actorDetails.popularity | number : "1.1-1" }}
        </div>
        <div class="actor-imdb-id">Imdb Id: {{ actorDetails.imdb_id }}</div>
      </div>
    </ng-component>
  </div>
</div>
