import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
// three movies in the slide

NowPlaying:any;

  //importing httpclient as http 
constructor(private http:HttpClient){

  this.getUpCommingThreeMovie()
}

// function to get upcomming three movies
getUpCommingThreeMovie(){

  this.http.get<any>("https://api.themoviedb.org/3/movie/popular?api_key=c3a444538ac89d0f62cf2c1a5f74f6eb").subscribe((data)=>{
    this.NowPlaying=data.results
    console.log(this.NowPlaying)
  })

}  

}
<br />
<h1>Now Playing</h1>
<br />
<hr />
<div class="my-container">
  <div class="box" *ngFor="let nowMovie of NowPlaying">
    <div class="movie-image">
      <img src="assets/images.jpg" alt="" />
      <div class="rating">75</div>
    </div>
    <div class="movie-content">
      <div class="movie-title">{{ nowMovie.original_title }}</div>
      <div class="movie-release-day">{{ nowMovie.release_date }}</div>
    </div>
  </div>
</div>
