import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MoviesLService } from '../services/movies-l.service';

@Component({
  selector: 'app-similar-movies',
  templateUrl: './similar-movies.component.html',
  styleUrls: ['./similar-movies.component.css']
})
export class SimilarMoviesComponent implements OnInit{
// similar movies data
similarMovies:any;

// movie id

moveId:any;
constructor(private active:ActivatedRoute, private movieList:MoviesLService){}

  ngOnInit(): void {
    // getting movie id from route
    this.active.params.subscribe((movieid)=>{
      this.moveId=movieid['id']
    })

    // getting similar movie using id
    this.movieList.getSimilarData(this.moveId).subscribe((data:any)=>{
      this.similarMovies=data.results
    })
  }

}
<div class="similar-movie-div row">
  <div class="col-md-4" *ngFor="let similarM of similarMovies">
    <div class="similar-movie-image-div">
      <img
        [src]="['https://image.tmdb.org/t/p/w500/' + similarM.poster_path]"
        width="100%"
        alt=""
      />
    </div>
    <div class="similar-movie-detail-div">
      <div class="movie-title">{{ similarM.title }}</div>
      <div class="movie-release-date">{{ similarM.release_date }}</div>
    </div>
  </div>
</div>
