import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { MoviesLService } from '../services/movies-l.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-popular-movies',
  templateUrl: './popular-movies.component.html',
  styleUrls: ['./popular-movies.component.css']
})
export class PopularMoviesComponent implements OnInit{

popularMovies:any;  

constructor(private http:HttpClient, private movielist:MoviesLService,private router:Router){

}  
ngOnInit(): void {
  this.movielist.getPopularMovies().subscribe((data)=>{
    this.popularMovies=data.results

  })
}

// getPopularMovies(){
//   this.http.get<any>("https://api.themoviedb.org/3/movie/popular?api_key=c3a444538ac89d0f62cf2c1a5f74f6eb").subscribe((data)=>{
//   this.popularMovies=data.results
//   })
// }  

navigateToDetails(id:any){
  this.router.navigate(['details/',id])


}



}
<h1>Popular Movies</h1>
<br />

<div class="my-container">
  <div class="box" *ngFor="let popularMovie of popularMovies">
    <div class="movie-image">
      <img
        [src]="['https://image.tmdb.org/t/p/w500/' + popularMovie.poster_path]"
        alt="{{ popularMovie.title }}"
        (click)="navigateToDetails(popularMovie.id)"
      />
      <div class="rating">
        {{ popularMovie.vote_average | number : "1.1-1" }}
      </div>
    </div>
    <div class="movie-content">
      <div class="movie-title">{{ popularMovie.original_title }}</div>
      <div class="movie-release-day">{{ popularMovie.release_date }}</div>
    </div>
  </div>
</div>
