// similar-movies.component.ts

import { Component, NgZone, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MoviesLService } from '../services/movies-l.service';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-similar-movies',
  templateUrl: './similar-movies.component.html',
  styleUrls: ['./similar-movies.component.css']
})
export class SimilarMoviesComponent implements OnInit {
  // similar movies data
  similarMovies:any;
datat:any;
  // movie id
  moveId: any;

  constructor(private active: ActivatedRoute, private movieList: MoviesLService, private zone: NgZone,private http:HttpClient) { }

  ngOnInit(): void {
    // getting movie id from route
    this.active.params.subscribe((movieid) => {
      this.moveId = movieid['id'];
    });

    // getting similar movie using id
    this.movieList.getSimilarData(this.moveId).subscribe((data: any) => {
      this.zone.run(() => {
        this.similarMovies = data.results;
      });
      console.log("this is similar", this.similarMovies);
    });
  }
getData(){
  this.http.get<any>('https://api.themoviedb.org/3/movie/520758/similar?api_key=c3a444538ac89d0f62cf2c1a5f74f6eb').subscribe((data)=>{
    this.datat=data.results
  })
}


}
<div class="similar-movie-div row">
  <div class="col-md-4" *ngFor="let movie of similarMovies">
    <div class="similar-movie-image-div">
      <img src="assets/images.jpg" width="100%" alt="" />
    </div>
    <div class="similar-movie-detail-div">
      <div class="movie-title">{{ movie.title }}</div>
      <div class="movie-release-date">alksdjg</div>
    </div>
  </div>
</div>
