import { HttpClient } from '@angular/common/http';
import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { MoviesLService } from '../services/movies-l.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-movie-videos',
  templateUrl: './movie-videos.component.html',
  styleUrls: ['./movie-videos.component.css']
})
export class MovieVideosComponent implements OnInit{
  // movie videos
movieVideos:any;
// movie id 
movieId:any;

constructor(private http:HttpClient, private movieList:MoviesLService,private active:ActivatedRoute,private cdr:ChangeDetectorRef){}


  ngOnInit(): void {
    // getting movie id
    this.active.params.subscribe((movieid:any)=>{
      this.movieId=movieid['id']
    })
    // getting movie videos
    this.movieList.getMovieVideos(this.movieId).subscribe((data)=>{
      this.movieVideos=data.results
      this.cdr.detectChanges();
    })
  }



}
<div class="movie-video-box-container row">
  <div class="row" *ngFor="let video of movieVideos">
    <div class="video-box col-md-4">
      <iframe
        width="100%"
        height="100%"
        [src]="['https://www.youtube.com/embed/' + video.key]"
      >
      </iframe>

      {{ video.movieVideos.key }}
    </div>
  </div>
</div>
