import { HttpClient } from '@angular/common/http';
import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MoviesLService } from '../services/movies-l.service';

@Component({
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.css']
})
export class MovieDetailComponent implements OnInit{

  @ViewChild('modal') modal:ElementRef |undefined

movieDetail:any;
movieId:any;
movieG:any;
movieImg:any;
movieCasts:any;
movieCrew:any;
movieVideos:any;
movieTrailer:any;
movieTrailerKey:number | undefined;
youtubeEmbedUrl:any;
  trailerObservable$: any;


constructor(private http:HttpClient,private activte:ActivatedRoute,private movieD:MoviesLService){
  this.activte.params.subscribe((value:any)=>{
  this.movieId=value['id']  
})
}



ngOnInit(): void {
  // to get movie details
  this.movieD.getMovieDetail(this.movieId).subscribe((data:any)=>{
    this.movieDetail=data 
    this.movieG=data.genres 
    this.movieImg=data.backdrop_path 
  })

  // to get movie cast details

  this.movieD.getMovieCast(this.movieId).subscribe((data)=>{
    this.movieCasts=data.cast
    this.movieCrew=data.crew

  })

  // to get movie videos
  this.movieD.getMovieVideos(this.movieId).subscribe((data)=>{
    this.movieVideos=data.results
    this.movieTrailer = this.movieVideos.filter((video:any) => video.type === "Trailer");
    console.log("trailer",this.movieTrailer)
    console.log("trailer video",this.movieTrailer[0].key )
     this.movieTrailerKey= this.movieTrailer[0].key 
     this.youtubeEmbedUrl='https://www.youtube.com/embed/'+this.movieTrailerKey
     console.log(this.movieTrailerKey)

  })
  this.modal?.nativeElement.addEventListener('shown.bs.modal', () => {
    this.trailerObservable$.subscribe((trailerKey: string) => {
      this.youtubeEmbedUrl = 'https://www.youtube.com/embed/' + trailerKey;
    });
  });







}

}
<div
  class="my-container row"
  [style.backgroundImage]="
    'url(https://image.tmdb.org/t/p/w500/' + movieDetail.backdrop_path + ')'
  "
>
  <div class="movie-image-div col-md-4">
    <img
      [src]="['https://image.tmdb.org/t/p/w500/' + movieDetail.poster_path]"
      alt=""
      id="poster"
    />
  </div>
  <div class="movie-details-div col-md-8">
    {{ youtubeEmbedUrl }}
    <div class="movie-title">{{ movieDetail.title }}</div>
    <div class="movie-release-date-genre-time">
      <span> Release Date: </span>{{ movieDetail.release_date }}.
      <div>
        <span> Genres: </span>
        <span class="gen" *ngFor="let gen of movieDetail.genres"
          >{{ gen.name }},
        </span>
      </div>
      {{ movieDetail.runtime }} runTime
    </div>
    <div class="movie-rating">
      <span> Rating: </span>{{ movieDetail.vote_average | number : "1.1-1" }}
    </div>
    <div class="movie-trailer-button">
      <button
        class="btn btn-warn"
        data-bs-toggle="modal"
        data-bs-target="#staticBackdrop"
      >
        <i class="fa-solid fa-film"></i>
        Trailer
      </button>
    </div>
    <div class="movie-overview">
      <span>Overview: </span>{{ movieDetail.overview }}
    </div>
  </div>
</div>

<!-- Modal -->

