<br />
<br />
<br />
<hr />
<div class="row">
  <div class="col-md-8">
    <h2>Top Cast</h2>
    <br />

    <div class="cast-div">
      <div class="cast" *ngFor="let cast of movieCasts">
        <div class="cast-image">
          <img
            [src]="['https://image.tmdb.org/t/p/w500/' + cast.profile_path]"
            alt="image not found"
          />
        </div>
        <div class="cast-detail">
          <h5>{{ cast.name }}</h5>
          <p>{{ cast.character }}</p>
        </div>
      </div>
    </div>
  </div>
  <div class="col-md-4"></div>
</div>
