import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit{

SearchText:any;
searchedMovies:any; 
seatchQuery:any;

constructor(private http:HttpClient, private active:ActivatedRoute,private router:Router){}

  ngOnInit(): void {
      // getting seach text
      this.active.params.subscribe((params)=>{
        this.SearchText=params['movie'];

        // console.log("this is search", this.SearchText)
      })
      // calling the search function to get the data
      this.gettingSeachMovies()


    }

    // getting movies of search
    gettingSeachMovies():void{
      if(this.SearchText){
        this.http.get<any>(`https://api.themoviedb.org/3/search/movie?query=${this.SearchText}&api_key=c3a444538ac89d0f62cf2c1a5f74f6eb`).subscribe(data=>{
          this.searchedMovies=data.results;
        }) 
      }
      console.log(this.SearchText)
      console.log(this.searchedMovies)

    }

    sanitizeSearchTerm():string{
      return this.SearchText.replace(/ /g,'+');
    }

    navigateDetails(id:any){
      this.router.navigate(['details/',id])
    }
}
<br />
<h1>Search Results</h1>
<br />

<div class="container">
  <div class="row">
    <h2 *ngIf="!searchedMovies" class="text-center">Not Found</h2>
    <ng-container *ngFor="let movie of searchedMovies">
      <div class="col-md-3 mb-3" *ngIf="movie.poster_path">
        <div class="search-movie-box">
          <div class="search-movie-image-div">
            <img
              [src]="['https://image.tmdb.org/t/p/w500/' + movie.poster_path]"
              alt=""
              (click)="navigateDetails(movie.id)"
            />
          </div>
          <div class="search-movie-content">
            <div class="search-movie-title">{{ movie.title }}</div>
            <div class="search-movie-release-date">
              {{ movie.release_date }}
            </div>
          </div>
        </div>
      </div>
    </ng-container>
  </div>
</div>
