import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MoviesLService } from '../services/movies-l.service';

@Component({
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.css']
})
export class MovieDetailComponent implements OnInit{
movieDetail:any;
movieId:any;
movieG:any;
constructor(private http:HttpClient,private activte:ActivatedRoute,private movieD:MoviesLService){
  this.activte.params.subscribe((value:any)=>{
  this.movieId=value['id']  
  })
}
  ngOnInit(): void {
   this.movieD.getMovieDetail(238).subscribe((data:any)=>{
   this.movieDetail=data 
   this.movieG=data.genres 
   })
  }


}
<div class="my-container">
  <div class="movie-image-div">
    <img src="assets/images.jpg" alt="" />
  </div>
  <div class="movie-details-div">
    <div class="movie-title">{{ movieDetail.title }}</div>
    <div class="movie-release-date-genre-time">
      Release Date: {{ movieDetail.release_date }}.
      <div *ngFor="let gen of movieDetail.genres">
        <span>{{ gen.name }}</span>
      </div>
      {{ movieDetail.runtime }} runTime
    </div>
    <div class="movie-rating">{{ movieDetail.vote_average }}</div>
    <div class="movie-trailer-button">trailer</div>
    <div class="movie-overview">
      {{ movieDetail.overview }}
    </div>
  </div>
</div>
