import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit{

SearchText:any;
searchedMovies:any; 
saniTizeSearch:any; 

constructor(private http:HttpClient, private active:ActivatedRoute){}

  ngOnInit(): void {
      // getting seach text
      this.active.params.subscribe((data)=>{
        this.SearchText=data['movie'];
        // console.log("this is search", this.SearchText)
      })
      // calling the search function to get the data
      this.gettingSeachMovies(this.SearchText)


    }

    // getting movies of search
    gettingSeachMovies(data:any){
      this.http.get<any>(`https://api.themoviedb.org/3/search/movie?query=${{data}}&api_key=c3a444538ac89d0f62cf2c1a5f74f6eb`).subscribe((data)=>{
        this.searchedMovies=data.results
      })
    }



}
<br />
<h1>Search Results</h1>
<br />

<div class="container">
  <div class="row">
    <div class="col-md-3" *ngFor="let movie of searchedMovies">
      <div class="search-movie-box">
        <div class="search-movie-image-div">
          <img
            [src]="['https://image.tmdb.org/t/p/w500/' + movie.poster_path]"
            alt=""
          />
        </div>
        <div class="search-movie-content">
          <div class="search-movie-title">{{ movie.title }}</div>
          <div class="search-movie-release-date">{{ movie.release_date }}</div>
        </div>
      </div>
    </div>
  </div>
</div>
