import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ActivatedRouteSnapshot } from '@angular/router';

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent implements OnInit{

actorDetails:any;
actorId:any;
constructor(private http:HttpClient, private active:ActivatedRoute){}  
  ngOnInit(): void {

    this.active.params.subscribe((id)=>{
      this.actorId=id['id']
    })


    this.http.get<any>(`https://api.themoviedb.org/3/person/2217977?api_key=c3a444538ac89d0f62cf2c1a5f74f6eb`).subscribe((data)=>{
      this.actorDetails=data
      console.log(this.actorDetails)
      console.log(this.actorId)
    })

  }

}
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <img src="assets/images" alt="" />
    </div>

    <div class="col-md-8">
      <div class="actor-name"></div>
      <div class="known-as"></div>
      <div class="actor-biography"></div>
      <div class="actor-birthday"></div>
      <div class="actor-deathday"></div>
      <div class="actor-gent"></div>
      <div class="department"></div>
      <div class="birth-place"></div>
      <div class="popularity"></div>
    </div>
  </div>
</div>
